mapper.select:
        SELECT


--         级联表的字段
        ,shouji.shouji_name as shoujiName
        ,shouji.shouji_photo as shoujiPhoto
        ,shouji.shouji_types as shoujiTypes
        ,shouji.shouji_kucun_number as shoujiKucunNumber
        ,shouji.shouji_price as shoujiPrice
        ,shouji.shouji_old_money as shoujiOldMoney
        ,shouji.shouji_new_money as shoujiNewMoney
        ,shouji.shouji_clicknum as shoujiClicknum
        ,shouji.shangxia_types as shangxiaTypes
        ,shouji.shouji_delete as shoujiDelete
        ,shouji.shouji_content as shoujiContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney
        ,yonghu.yonghu_sum_jifen as yonghuSumJifen
        ,yonghu.yonghu_new_jifen as yonghuNewJifen
        ,yonghu.huiyuandengji_types as huiyuandengjiTypes

        FROM cart  a
        left JOIN shouji shouji ON a.shouji_id = shouji.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and (
                    a.shouji_id = #{params.shoujiId}
                )


                  and a.buy_number >= #{params.buyNumberStart}   


                  and a.buy_number <= #{params.buyNumberEnd}   


                and a.buy_number = #{params.buyNumber}


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.shouji_id IS NOT NULL


                and shouji.shouji_name like CONCAT('%',#{params.shoujiName},'%')


                and shouji.shouji_types = #{params.shoujiTypes}



                  and shouji.shouji_kucun_number >= #{params.shoujiKucunNumberStart}   


                  and shouji.shouji_kucun_number <= #{params.shoujiKucunNumberEnd}   


                and shouji.shouji_kucun_number = #{params.shoujiKucunNumber}


                  and shouji.shouji_price >= #{params.shoujiPriceStart}   


                  and shouji.shouji_price <= #{params.shoujiPriceEnd}   


                and shouji.shouji_price = #{params.shoujiPrice}


                  and shouji.shouji_old_money >= #{params.shoujiOldMoneyStart}   


                  and shouji.shouji_old_money <= #{params.shoujiOldMoneyEnd}   


                  and shouji.shouji_new_money >= #{params.shoujiNewMoneyStart}   


                  and shouji.shouji_new_money <= #{params.shoujiNewMoneyEnd}   


                  and shouji.shouji_clicknum >= #{params.shoujiClicknumStart}   


                  and shouji.shouji_clicknum <= #{params.shoujiClicknumEnd}   


                and shouji.shouji_clicknum = #{params.shoujiClicknum}


                and shouji.shangxia_types = #{params.shangxiaTypes}



                  and shouji.shouji_delete >= #{params.shoujiDeleteStart}   


                  and shouji.shouji_delete <= #{params.shoujiDeleteEnd}   


                and shouji.shouji_delete = #{params.shoujiDelete}


                and shouji.shouji_content like CONCAT('%',#{params.shoujiContent},'%')



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   


                  and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


                  and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


                  and yonghu.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   


                  and yonghu.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   


                and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}




        order by a.${params.orderBy} desc
