mapper.select:
        SELECT


--         级联表的字段

        FROM shouji  a



                and a.id in

                #{item}



                and a.shouji_name like CONCAT('%',#{params.shoujiName},'%')


                and a.shouji_types = #{params.shoujiTypes}


                  and a.shouji_kucun_number >= #{params.shoujiKucunNumberStart}   


                  and a.shouji_kucun_number <= #{params.shoujiKucunNumberEnd}   


                and a.shouji_kucun_number = #{params.shoujiKucunNumber}


                  and a.shouji_price >= #{params.shoujiPriceStart}   


                  and a.shouji_price <= #{params.shoujiPriceEnd}   


                and a.shouji_price = #{params.shoujiPrice}


                  and a.shouji_old_money >= #{params.shoujiOldMoneyStart}   


                  and a.shouji_old_money <= #{params.shoujiOldMoneyEnd}   


                  and a.shouji_new_money >= #{params.shoujiNewMoneyStart}   


                  and a.shouji_new_money <= #{params.shoujiNewMoneyEnd}   


                  and a.shouji_clicknum >= #{params.shoujiClicknumStart}   


                  and a.shouji_clicknum <= #{params.shoujiClicknumEnd}   


                and a.shouji_clicknum = #{params.shoujiClicknum}


                and a.shangxia_types = #{params.shangxiaTypes}


                  and a.shouji_delete >= #{params.shoujiDeleteStart}   


                  and a.shouji_delete <= #{params.shoujiDeleteEnd}   


                and a.shouji_delete = #{params.shoujiDelete}


                and a.shouji_content like CONCAT('%',#{params.shoujiContent},'%')




        order by a.${params.orderBy} desc
